import {fromEvent, takeUntil, throttleTime} from "rxjs";

console.log('rxjs')

const btn = document.getElementById('btn')

fromEvent(document,'mousemove')
        .pipe(
            throttleTime(2000)
        )
        .subscribe(console.log)


console.log('rxjs')
